import { useState } from "react"
import Button from "../../components/button"
import Input from "../../components/input"

const Form = ({
    isSignInPage = false,
}) => {
  const [data, setData] = useState({
    ...(!isSignInPage && {
      fullName: ''
    }),
    email: '',
    password: ''
  })

  console.log('data :>> ', data);
  return (
    <div className=" bg-white w-[500px] h-[600px] shadow-lg rounded-lg flex flex-col justify-center items-center">
        <div className=" text-4xl font-extrabold">Welcome {isSignInPage && 'Back'}</div>
        <div className=" text-xl font-light mb-14">{isSignInPage ? 'Sign in to get explored' : 'Sign up to get started'}</div>
        { !isSignInPage && <Input label="Full name" name="name" placeholder="Enter your full name" className="mb-6" value={data.fullName} onChange={(e) => setData({ ...data, fullName: e.target.value})}/> }
        <Input label="Email address" name="email" placeholder="Enter your email" className="mb-6" value={data.email} onChange={(e) => setData({ ...data, email: e.target.value})}/>
        <Input label="Password" name="password" placeholder="Enter your Password" className="mb-14" value={data.password} onChange={(e) => setData({ ...data, fpassword: e.target.value})}/>
        <Button label={isSignInPage ? 'Sign in' : 'Sign up'} className="w-1/2 mb-2"/>
        <div>{ isSignInPage ? "Don't have an account?" : "Already have an account?"} <span className=" text-primary cursor-pointer underline">{ isSignInPage ? 'Sign up' : 'Sign in'}</span></div>
    </div>
  )
}

export default Form
